import random

# List of possible responses for different inputs
responses = {
    "hello": "Hello! How can I assist you today?",
    "hi": "Hi there! What seems to be the problem?",
    "how are you?": "I'm just a bot, but I'm here to help! How about you?",
    "depressed": "It's important to talk about your feelings. Have you talked to someone about this?",
    "tired": "Getting enough sleep is crucial for good health. What can we do to improve your sleep routine?",
    "hungry": "Make sure to eat balanced meals and stay hydrated.",
    "bye": "Goodbye! Take care!",
    "default": "I'm not sure how to respond to that. Can you please elaborate or rephrase?"
}

def get_response(user_input):
    user_input = user_input.lower()
    if user_input in responses:
        return responses[user_input]
    else:
        return responses["default"]

def main():
    print("Welcome to the Doctor Chatbot! Type 'bye' to exit.")
    while True:
        user_input = input("You: ")
        if user_input.lower() == "bye":
            print(get_response(user_input))
            break
        else:
            response = get_response(user_input)
            print("Bot:", response)

if __name__ == "__main__":
    main()
